mapper.select:
	SELECT 
		*
	FROM 
		ANNOTATION_NEW a 
	WHERE
		a.record_id = #{recordId}

		and a.RECORD_SEQ = #{recordSeq}


		and a.RECORD_SEQ = (
			SELECT r.RECORD_SEQ
			FROM RECORD_NEW r
			WHERE r.record_id = #{recordId}
		)


		and a.content in

              #{keyword}


	ORDER BY a.start_point+0

mapper.insert:
INSERT INTO 
	ANNOTATION_NEW (ANN_ID, NAME, START_POINT, END_POINT, CONTENT, RECORD_ID, RECORD_SEQ) 
VALUES (
	#{annId}, #{name}, #{startPoint}, #{endPoint}, #{content}, #{recordId},
	(
		SELECT RECORD_SEQ
		FROM RECORD_NEW
		WHERE RECORD_ID = #{recordId}
	)
)
