mapper.select:
SELECT a2.content as content, count(a2.content) as count FROM ANNOTATION_NEW a inner join RECORD_NEW r on a.RECORD_ID = r.RECORD_ID and a.RECORD_SEQ = r.RECORD_SEQ and r.GROUP_NAME = #{groupName} and a.NAME in ( select name from RELATION where rel_id in
${relId}
) inner join ANNOTATION_NEW a2 on a.START_POINT = a2.ANN_ID AND a2.RECORD_ID = a.RECORD_ID AND a2.RECORD_SEQ = a.RECORD_SEQ WHERE 1=1
AND a2.CONTENT like CONCAT('%',#{searchTerm},'%')
GROUP BY a2.content ORDER BY a2.content DESC

SELECT a2.content as content, count(a2.content) as count FROM ANNOTATION_NEW a inner join RECORD_NEW r on a.RECORD_ID = r.RECORD_ID and a.RECORD_SEQ = r.RECORD_SEQ and r.GROUP_NAME = #{groupName} and a.NAME in ( select name from RELATION where rel_id in
${relId}
) inner join ANNOTATION_NEW a2 on a.END_POINT = a2.ANN_ID AND a2.RECORD_ID = a.RECORD_ID AND a2.RECORD_SEQ = a.RECORD_SEQ WHERE 1=1
AND a2.CONTENT like CONCAT('%',#{searchTerm},'%')
GROUP BY a2.content ORDER BY a2.content DESC

SELECT count(a.RECORD_ID) as count, r.DOC_ID, r.RECORD_ID, (SELECT d.subject FROM DOCUMENT_NEW d WHERE d.doc_id = r.DOC_ID) as subject FROM ANNOTATION_NEW a inner join RECORD_NEW r on a.RECORD_ID = r.RECORD_ID and a.RECORD_SEQ = r.RECORD_SEQ and r.GROUP_NAME = #{groupName} inner join RELATION rel on a.NAME = rel.NAME and rel.REL_ID in
${relId}
inner join ANNOTATION_NEW a2 on a.start_point = a2.ANN_ID AND a2.RECORD_ID = a.RECORD_ID AND a2.RECORD_SEQ = a.RECORD_SEQ
inner join ANNOTATION_NEW a3 on a.end_point = a3.ANN_ID AND a3.RECORD_ID = a.RECORD_ID AND a3.RECORD_SEQ = a.RECORD_SEQ
WHERE 1=1
and a2.CONTENT = #{startPoint}
and a3.CONTENT = #{endPoint}
group by a.RECORD_ID

SELECT a.ANN_ID as REL_ID
,a2.ANN_ID as START_ENT
,a3.ANN_ID as END_ENT
FROM ANNOTATION_NEW a
inner join ANNOTATION_NEW a2 on a.start_point = a2.ANN_ID AND a2.RECORD_ID = a.RECORD_ID AND a2.RECORD_SEQ = a.RECORD_SEQ
inner join ANNOTATION_NEW a3 on a.end_point = a3.ANN_ID AND a3.RECORD_ID = a.RECORD_ID AND a3.RECORD_SEQ = a.RECORD_SEQ
WHERE 1=1
and a2.CONTENT = #{startPoint}
and a3.CONTENT = #{endPoint}
and a.NAME in ( select name from RELATION where rel_id in
${relId}
) AND a.RECORD_ID = #{recordId} AND a.RECORD_SEQ = ( SELECT r.RECORD_SEQ FROM RECORD_NEW r WHERE r.RECORD_ID = #{recordId} )
