mapper.insert:
INSERT INTO 
	JOB_HISTORY (RECORD_ID, RECORD_SEQ, TYPE, JOB, REG_ID, NOTE) 
SELECT
	#{recordId}, record_seq, #{type},#{job},#{userId},#{note}
FROM RECORD_NEW
WHERE record_id = #{recordId}

INSERT INTO 
	JOB_HISTORY (TYPE, JOB, REG_ID, NOTE) 
VALUES (
	#{type},#{job},#{userId},#{note}
)
