mapper.update:
UPDATE ENTITY 
SET 
	NAME = #{name}
	, LABELS = #{label}
	, BGCOLOR = #{bgColor}
WHERE 
	ENT_ID = #{entId}

mapper.select:
SELECT r.record_id, r.rabel_stat, c.NAME as domain, d.SUBJECT as subject, left(d.CONTENT,200) as content, d.REG_ID, d.REG_DATE, r.CONF_ID, r.group_name FROM ANNOTATION_NEW a INNER JOIN RECORD_NEW r ON a.RECORD_ID = r.RECORD_ID AND a.RECORD_SEQ = r.RECORD_SEQ INNER JOIN DOCUMENT_NEW d on d.DOC_ID = r.DOC_ID INNER JOIN COLLECTION_NEW c ON c.COL_ID = d.COL_ID WHERE 1=1
AND a.NAME = #{name}
AND r.GROUP_NAME = #{groupName}
GROUP BY a.RECORD_ID ORDER BY r.REG_DATE desc LIMIT ${pageSize} OFFSET ${offSet}

SELECT a.RECORD_ID FROM ANNOTATION_NEW a INNER JOIN RECORD_NEW r ON a.RECORD_ID = r.RECORD_ID AND a.RECORD_SEQ = r.RECORD_SEQ INNER JOIN DOCUMENT_NEW d on d.DOC_ID = r.DOC_ID INNER JOIN COLLECTION_NEW c ON c.COL_ID = d.COL_ID WHERE 1=1
AND a.NAME = #{name}
AND r.GROUP_NAME = #{groupName}
GROUP BY a.RECORD_ID

	SELECT 
		*
	FROM 
		ANNOTATION_NEW a 
	WHERE
		a.record_id = #{recordId}
		and a.RECORD_SEQ = (
			SELECT r.RECORD_SEQ
			FROM RECORD_NEW r
			WHERE r.record_id = #{recordId}
		)


		and a.NAME != #{name}
  		and a.START_POINT != #{name}
        and a.END_POINT != #{name}   


	ORDER BY a.start_point+0

SELECT
	*
FROM 
	RELATION
WHERE 
	group_name = #{groupName}
	and name = #{name}
	and start_ent = #{startRel}

mapper.update:
UPDATE RELATION 
SET 
	START_ENT = #{startRel}
	, END_ENT = #{endRel}
	, LABELS = #{label}
WHERE 
	REL_ID = #{relId}

mapper.insert:
INSERT INTO 
	RELATION (NAME, GROUP_NAME, PARENT_REL, START_ENT, END_ENT, LABELS) 
VALUES (
	#{name},#{groupName},#{parentRel}, #{startRel}, #{endRel}, #{label}
	)

mapper.delete:
DELETE FROM RELATION
WHERE 
	1=1
	AND NAME = #{name}
	AND GROUP_NAME = #{groupName}

		AND REL_ID not in 

		#{relId}
